import styles from "./app.module.css";
import { data } from "../../utils/data";
import AppHeader from "../AppHeader/AppHeader";
import BurgerIngredients from "../BurgerIngredients/BurgerIngredients";
import BurgerConstructor from "../BurgerConstructor/BurgerConstructor";
import Modal from "../Modal/Modal";
import React from "react";
import IngredientDetails from "../IngredientDetails/IngredientDetails";
import OrderDetails from "../OrderDetails/OrderDetails";

function App() {
  const [isOpenModalOrder, setOpenModalOrder] = React.useState(false);
  const closeModal = () => {
    setOpenModalOrder((isOpenModalOrder = false));
  };

  return (
    <>
      <AppHeader />
      <BurgerIngredients />
      <BurgerConstructor onClick={() => setOpenModalOrder(isOpenModalOrder = true)} />
      ({isOpenModalOrder && <OrderDetails />})
      {/* <Modal title="Детали ингредиента" onClose={closeModal}>
        
      </Modal> */}
      {/* <IngredientDetails data={data[0]} /> */}
    </>
  );
}

export default App;
