// import styles from "./BurgerIngredients.module.css";
import { data } from "../../utils/data";
import React from "react";
import { forwardRef, useEffect, useRef, useState } from "react";
import { Tab } from "@ya.praktikum/react-developer-burger-ui-components";
import { CurrencyIcon } from "@ya.praktikum/react-developer-burger-ui-components";
import { Counter } from "@ya.praktikum/react-developer-burger-ui-components";
import { ConstructorElement } from "@ya.praktikum/react-developer-burger-ui-components";
// import React, { useState } from "react";
// import bunImg from "../../images/bun-02.png";
// import bunImgScnd from "../../images/bun-01.png";
// import sauce from "../../images/sauce-02.png";
// import sauceScnd from "../../images/sauce-04.png";
// import sauceFourth from "../../images/sauce-01.png";
// import sauceFifth from "../../images/sauce-03.png";
import styles from "./BurgerIngredients.module.css";
import BurgerConstructor from "../BurgerConstructor/BurgerConstructor";
import ModalOverlay from "../ModalOverlay/ModalOverlay";
import { ingredientsApi } from "../../utils/api";

const { getIngredients } = ingredientsApi();

const IngredientsNames = {
  bun: "Булки",
  main: "Начинки",
  sauce: "Соусы",
};

const IngredientsNavigation = ({ clickCategory, currentType, refs }) => {
  const handlePick = (e) => {
    clickCategory(e);
    refs[e].current.scrollIntoView({ behavior: "smooth" });
  };

  return (
    <div className={styles.navigation}>
      <Tab value="bun" active={currentType === "bun"} onClick={handlePick}>
        Булки
      </Tab>
      <Tab value="sauce" active={currentType === "sauce"} onClick={handlePick}>
        Соусы
      </Tab>
      <Tab value="main" active={currentType === "main"} onClick={handlePick}>
        Начинки
      </Tab>
    </div>
  );
};

const BurgerIngredient = ({ element, count, addClick, onOpen }) => {
  const { image, price, name, _id, type } = element;

  const handleClick = () => {
    addClick(type, _id);
  };

  const handleOnOpen = () => {
    onOpen();
  };

  return (
    <li className={`${styles.ingredient} pt-6`} onClick={handleOnOpen}>
      <img src={image} alt={name} className="pl-4 pr-4" />
      <div className={`${styles.currency}`} onClick={handleClick}>
        <p className="text text_type_digits-default">{price} </p>
        <CurrencyIcon type={"primary"} />
      </div>
      <p className={`${styles.text} text text_type_main-default`}>{name}</p>
      {count && (
        <Counter
          count={count}
          size="default"
          extraClass={`${styles.counter}`}
        />
      )}
    </li>
  );
};

const Ingredients = forwardRef((props, ref) => {
  return (
    <div ref={ref} className={`${styles.ingredients} pt-10`}>
      <h2 className="text text_type_main-medium">{props.title}</h2>
      <ul className={`${styles.list}`}>
        {props.ingredients &&
          props.ingredients.map((ingredient) => (
            <BurgerIngredient
              key={ingredient._id}
              element={ingredient}
              addClick={props.addClick}
              count={props.ingredientsCount[ingredient._id]}
              onOpen={props.onOpen}
            />
          ))}
      </ul>
    </div>
  );
});

function BurgerIngredients({ onOpen, ingredients }) {
  const bunRef = useRef(null);
  const mainRef = useRef(null);
  const sauceRef = useRef(null);

  const [bunCounter, setBun] = useState({});
  const [mainCounter, setMain] = useState({});
  const [sauceCounter, setSauce] = useState({});

  const [state, setState] = useState({
    bunMas: [],
    mainMas: [],
    sauceMas: [],
  });

  const [currentType, setCurrentType] = useState("bun");

  // useEffect(() => {
  //   setState({
  //     bunMas: data.filter((item) => item.type === "bun"),
  //     mainMas: data.filter((item) => item.type === "main"),
  //     sauceMas: data.filter((item) => item.type === "sauce"),
  //   });
  // }, []);

  React.useEffect(() => {
    getIngredients()
      .then((res, good) => {
        if (good) {
          setState({
            bunMas: res.filter((item) => item.type === "bun"),
            mainMas: res.filter((item) => item.type === "main"),
            sauceMas: res.filter((item) => item.type === "sauce"),
          });
        } else {
          console.log("Упс, что-то сломалось");
        }
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const handleClick = (type, id) => {
    if (type === "bun") {
      setBun({ ...bunCounter, [id]: bunCounter[id] ? bunCounter[id] + 1 : 1 });
    } else if (type === "main") {
      setMain({
        ...mainCounter,
        [id]: mainCounter[id] ? mainCounter[id] + 1 : 1,
      });
    } else if (type === "sauce") {
      setSauce({
        ...sauceCounter,
        [id]: sauceCounter[id] ? sauceCounter[id] + 1 : 1,
      });
    }
  };

  const pickType = (type) => {
    setCurrentType(type);
  };

  return (
    <section className={`${styles.container} pt-10`}>
      <h1 className="text text_type_main-large mb-5">Соберите бургер</h1>
      <IngredientsNavigation
        refs={{ bun: bunRef, main: mainRef, sauce: sauceRef }}
        clickCategory={pickType}
        currentType={currentType}
      />
      <div className={`${styles.scroll} custom-scroll`}>
        <Ingredients
          ref={bunRef}
          ingredientsCount={bunCounter}
          ingredients={ingredients.bun}
          title={IngredientsNames["bun"]}
          addClick={handleClick}
          onOpen={onOpen}
        />
        <Ingredients
          ref={sauceRef}
          ingredientsCount={sauceCounter}
          ingredients={state.sauceMas}
          title={IngredientsNames["sauce"]}
          addClick={handleClick}
          onOpen={onOpen}
        />
        <Ingredients
          ref={mainRef}
          ingredientsCount={mainCounter}
          ingredients={state.mainMas}
          title={IngredientsNames["main"]}
          addClick={handleClick}
          onOpen={onOpen}
        />
      </div>
    </section>
  );
}

export default BurgerIngredients;
