import {
  ConstructorElement,
  CurrencyIcon,
} from "@ya.praktikum/react-developer-burger-ui-components";
import { data } from "../../utils/data";

function BurgerConstructor() {
  return (
    <>
    <section
      className="mt-25 mb-10 ml-5 mr-5"
    >
      <ul
        className="pl-8 pr-8"
        style={{
          listStyleType: "none",
          display: "flex",
          flexDirection: "column",
          gap: "16px",
          alignItems: "center",
        }}
      >
        <li>
          <ConstructorElement
            type="top"
            isLocked={true}
            text="Краторная булка N-200i (верх)"
            price={200}
            thumbnail={data[0].image}
          />
        </li>
        <li>
          <ConstructorElement
            text="Краторная булка N-200i (верх)"
            price={50}
            thumbnail={data[0].image}
          />
        </li>
        <li>
          <ConstructorElement
            text="Краторная булка N-200i (низ)"
            price={200}
            thumbnail={data[0].image}
          />
        </li>
        <li>
          <ConstructorElement
            text="Краторная булка N-200i (низ)"
            price={200}
            thumbnail={data[0].image}
          />
        </li>
        <li>
          <ConstructorElement
            text="Краторная булка N-200i (низ)"
            price={200}
            thumbnail={data[0].image}
          />
        </li>
        <li>
          <ConstructorElement
            text="Краторная булка N-200i (низ)"
            price={200}
            thumbnail={data[0].image}
          />
        </li>
        <li>
          <ConstructorElement
            type="bottom"
            isLocked={true}
            text="Краторная булка N-200i (низ)"
            price={200}
            thumbnail={data[0].image}
          />
        </li>
      </ul>
    </section>
    <section style={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
    <span
        className="text text_type_digits-medium"
        style={{
          display: "inline-flex",
          alignItems: "center",
          justifyContent: "flex-end",
          textAlign: "right",
          gap: "8px",
        }}
      >
        610
        <CurrencyIcon type="primary" />
      </span>
    </section>
    </>
  );
}

export default BurgerConstructor;
