// import styles from "./BurgerIngredients.module.css";
import { data } from "../../utils/data";
import { Tab } from "@ya.praktikum/react-developer-burger-ui-components";
import { CurrencyIcon } from "@ya.praktikum/react-developer-burger-ui-components";
import { Counter } from "@ya.praktikum/react-developer-burger-ui-components";
import { ConstructorElement } from "@ya.praktikum/react-developer-burger-ui-components";
import React, { useState } from "react";
// import bunImg from "../../images/bun-02.png";
// import bunImgScnd from "../../images/bun-01.png";
// import sauce from "../../images/sauce-02.png";
// import sauceScnd from "../../images/sauce-04.png";
// import sauceFourth from "../../images/sauce-01.png";
// import sauceFifth from "../../images/sauce-03.png";
import styles from "./BurgerIngredients.module.css";
import BurgerConstructor from "../BurgerConstructor/BurgerConstructor";
import ModalOverlay from "../ModalOverlay/ModalOverlay";

const BurgerIngredient = ({ element, count, handleClick }) => {
  const { price, image, name, _id, type } = element;

  const onClick = () => {
    handleClick(type, _id);
  };

  return (
    <li className={`${styles.ingredient} pt-6`}>
      <img src={image} alt={name} className="pl-4 pr-4" />
      <div>
        <p>{price}</p>
        <CurrencyIcon />
      </div>
      <p>{name}</p>
      {count && (
        <Counter
          count={count}
          size="default"
          extraClass={`${styles.counter}`}
        />
      )}
    </li>
  );
};

// function BurgerIngredients() {
