import {
  ConstructorElement,
  CurrencyIcon,
  Button,
  DragIcon,
} from "@ya.praktikum/react-developer-burger-ui-components";
import { data } from "../../utils/data";

function BurgerConstructor() {
  return (
    <section
      className="mt-25 mb-10 ml-5 mr-5"
      style={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        gap: "40px",
      }}
    >
      <ul
        style={{
          listStyleType: "none",
          display: "flex",
          flexDirection: "column",
          gap: "16px",
          alignItems: "center",
          overflowY: "auto",
        }}
      >
        <li className="ml-8">
          <ConstructorElement
            type="top"
            isLocked={true}
            text="Краторная булка N-200i (верх)"
            price={200}
            thumbnail={data[0].image}
          />
        </li>
        <li style={{ display: "flex", gap: "8px", alignItems: "center" }}>
          <DragIcon type="primary" />
          <ConstructorElement
            text="Краторная булка N-200i (верх)"
            price={50}
            thumbnail={data[0].image}
          />
        </li>
        <li style={{ display: "flex", gap: "8px", alignItems: "center" }}>
          <ConstructorElement
            text="Краторная булка N-200i (низ)"
            price={200}
            thumbnail={data[0].image}
          />
        </li>
        <li style={{ display: "flex", gap: "8px", alignItems: "center" }}>
          <ConstructorElement
            text="Краторная булка N-200i (низ)"
            price={200}
            thumbnail={data[0].image}
          />
        </li>
        <li style={{ display: "flex", gap: "8px", alignItems: "center" }}>
          <ConstructorElement
            text="Краторная булка N-200i (низ)"
            price={200}
            thumbnail={data[0].image}
          />
        </li>
        <li style={{ display: "flex", gap: "8px", alignItems: "center" }}>
          <ConstructorElement
            text="Краторная булка N-200i (низ)"
            price={200}
            thumbnail={data[0].image}
          />
        </li>
        <li>
          <ConstructorElement
            type="bottom"
            isLocked={true}
            text="Краторная булка N-200i (низ)"
            price={200}
            thumbnail={data[0].image}
          />
        </li>
      </ul>
      <div>
        <span
          className="text text_type_digits-medium mr-10 ml-8 mr-8"
          style={{
            display: "inline-flex",
            alignItems: "center",
            justifyContent: "flex-end",
            textAlign: "right",
            gap: "8px",
          }}
        >
          610
          <CurrencyIcon type="primary" />
        </span>
        <Button htmlType="button" type="primary" size="large">
          Оформить заказ
        </Button>
      </div>
    </section>
  );
}

export default BurgerConstructor;
