import styles from "./app.module.css";
import { data } from "../../utils/data";
import AppHeader from "../AppHeader/AppHeader";
import BurgerIngredients from "../BurgerIngredients/BurgerIngredients";
import BurgerConstructor from "../BurgerConstructor/BurgerConstructor";
import Modal from "../Modal/Modal";
import React from "react";
import IngredientDetails from "../IngredientDetails/IngredientDetails";
import OrderDetails from "../OrderDetails/OrderDetails";
import { ingredientsApi } from "../../utils/api";

function App() {
  const { getIngredients } = ingredientsApi();

  const [ingredients, setIngredients] = React.useState({
    buns: [],
    mains: [],
    sauces: [],
  });

  // React.useEffect(() => {
  //   getIngredients()
  //     .then((res, good) => {
  //       if (good) {
  //         setIngredients({
  //           buns: res.filter((item) => item.type === "bun"),
  //           mains: res.filter((item) => item.type === "main"),
  //           sauces: res.filter((item) => item.type === "sauce"),
  //         });
  //       } else {
  //         console.log("Упс, что-то сломалось");
  //       }
  //     })
  //     .catch((err) => {
  //       console.log(err);
  //     });
  // }, []);

  const [isOpenModalOrder, setOpenModalOrder] = React.useState(false);

  const handleModalOrder = () => {
    setOpenModalOrder(isOpenModalOrder ? false : true);
  };

  const [ingredientOpen, setOpenIngredient] = React.useState(false);

  const ingredientOpener = () => {
    setOpenIngredient(ingredientOpen ? false : true);
  };

  return (
    <>
      <AppHeader />
      <BurgerIngredients onOpen={ingredientOpener} ingredients={ingredients} />
      <BurgerConstructor onClick={handleModalOrder} />(
      {isOpenModalOrder && <OrderDetails onClick={handleModalOrder} />}) (
      {ingredientOpen && (
        <IngredientDetails data={data[0]} onClose={ingredientOpener} />
      )}
      )
      {/* <Modal title="Детали ингредиента" onClose={closeModal}>
        
      </Modal> */}
      {/* <IngredientDetails data={data[0]} /> */}
    </>
  );
}

export default App;
