import styles from "./BurgerIngredients.module.css";
import { data } from "../../utils/data";
import { Tab } from "@ya.praktikum/react-developer-burger-ui-components";
import { CurrencyIcon } from "@ya.praktikum/react-developer-burger-ui-components";
import { Counter } from "@ya.praktikum/react-developer-burger-ui-components";
import { ConstructorElement } from "@ya.praktikum/react-developer-burger-ui-components";
import React, { useState } from "react";
import bunImg from "../../images/bun-02.png";

function BurgerIngredients() {
  const [current, setCurrent] = React.useState("two");

  return (
    <main style={{ display: "flex", justifyContent: "center" }}>
      <div className="mt-10">
        <h1 className="text text_type_main-large mb-5">Соберите бургер</h1>
        <div style={{ display: "flex" }}>
          <Tab value="one" active={current === "one"} onClick={setCurrent}>
            One
          </Tab>
          <Tab value="two" active={current === "two"} onClick={setCurrent}>
            Two
          </Tab>
          <Tab value="three" active={current === "three"} onClick={setCurrent}>
            Three
          </Tab>
        </div>
        <section className="mt-10">
          <h2 className="text text_type_main-medium m-6">Булки</h2>
          <div
            className=""
            style={{
              width: "min-content",
              position: "relative",
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
            }}
          >
            <img src={bunImg} alt="краторная булка" />
            <div style={{display: "flex"}}>
            <p className="text text_type_digits-medium">20</p>
            <CurrencyIcon type="primary" />
            </div>
            <Counter count={1} size="default" extraClass="m-1" />
          </div>
        </section>
      </div>
    </main>
  );
}

export default BurgerIngredients;
