// import styles from "./BurgerIngredients.module.css";
import { data } from "../../utils/data";
import { Tab } from "@ya.praktikum/react-developer-burger-ui-components";
import { CurrencyIcon } from "@ya.praktikum/react-developer-burger-ui-components";
import { Counter } from "@ya.praktikum/react-developer-burger-ui-components";
import { ConstructorElement } from "@ya.praktikum/react-developer-burger-ui-components";
import React, { useState } from "react";
import bunImg from "../../images/bun-02.png";
import bunImgScnd from "../../images/bun-01.png";
import sauce from "../../images/sauce-02.png";
import sauceScnd from "../../images/sauce-04.png";
import sauceFourth from "../../images/sauce-01.png";
import sauceFifth from "../../images/sauce-03.png";
import styles from "./BurgerIngredients.module.css";
import BurgerConstructor from "../BurgerConstructor/BurgerConstructor";
import ModalOverlay from "../ModalOverlay/ModalOverlay";
function BurgerIngredients() {
  const [current, setCurrent] = React.useState("Булки");

  function openModal() {
    return <ModalOverlay />;
  }

  return (
    <main style={{ display: "flex", justifyContent: "center" }}>
      <div className="mt-10">
        <h1 className="text text_type_main-large mb-5">Соберите бургер</h1>
        <div style={{ display: "flex" }}>
          <Tab value="Булки" active={current === "Булки"} onClick={setCurrent}>
            Булки
          </Tab>
          <Tab value="Соусы" active={current === "Соусы"} onClick={setCurrent}>
            Соусы
          </Tab>
          <Tab
            value="Начинки"
            active={current === "Начинки"}
            onClick={setCurrent}
          >
            Начинки
          </Tab>
        </div>
        <section className={styles.scroll}>
          <h2 className="text text_type_main-medium mb-6">Булки</h2>
          <div
            className="pt-6 pl-4"
            style={{
              display: "grid",
              gridTemplateColumns: "1fr 1fr",
              gridTemplateRows: "1fr",
            }}
          >
            <div
            onClick={this.openModal}
              className="mb-10"
              style={{
                width: "min-content",
                position: "relative",
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
              }}
            >
              <img src={data[0].image} alt="краторная булка" />
              <div style={{ display: "flex" }} className="mt-2 mb-2">
                <p className="text text_type_digits-default mr-2">20</p>
                <CurrencyIcon type="primary" />
              </div>
              <p className="text text_type_main-small pb-6">
                Краторная булка N-200i
              </p>
              <Counter count={1} size="default" extraClass="m-1" />
            </div>
            <div
              className="mb-10"
              style={{
                width: "min-content",
                position: "relative",
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
              }}
            >
              <img src={bunImgScnd} alt="краторная булка" />
              <div style={{ display: "flex" }} className="mt-2 mb-2">
                <p className="text text_type_digits-default mr-2">20</p>
                <CurrencyIcon type="primary" />
              </div>
              <p className="text text_type_main-small pb-6">
                Флюоресцентная булка R2-D3
              </p>
              {/* <Counter count={1} size="default" extraClass="m-1" /> */}
            </div>
          </div>
          <h2 className="text text_type_main-medium mb-6">Соусы</h2>
          <div
            className="pt-6 pl-4"
            style={{
              display: "grid",
              gridTemplateColumns: "1fr 1fr",
              gridTemplateRows: "1fr 1fr 1fr",
            }}
          >
            <div
              className="mb-10"
              style={{
                width: "min-content",
                position: "relative",
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
              }}
            >
              <img src={sauce} alt="краторная булка" />
              <div style={{ display: "flex" }} className="mt-2 mb-2">
                <p className="text text_type_digits-default mr-2">30</p>
                <CurrencyIcon type="primary" />
              </div>
              <p className="text text_type_main-small pb-6">Соус Spicy-X</p>
              {/* <Counter count={1} size="default" extraClass="m-1" /> */}
            </div>
            <div
              className="mb-10"
              style={{
                width: "min-content",
                position: "relative",
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
              }}
            >
              <img src={sauceScnd} alt="краторная булка" />
              <div style={{ display: "flex" }} className="mt-2 mb-2">
                <p className="text text_type_digits-default mr-2">30</p>
                <CurrencyIcon type="primary" />
              </div>
              <p className="text text_type_main-small pb-6">
                Соус фирменный Space Sauce
              </p>
              {/* <Counter count={1} size="default" extraClass="m-1" /> */}
            </div>
            <div
              className="mb-10"
              style={{
                width: "min-content",
                position: "relative",
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
              }}
            >
              <img src={sauceFifth} alt="краторная булка" />
              <div style={{ display: "flex" }} className="mt-2 mb-2">
                <p className="text text_type_digits-default mr-2">30</p>
                <CurrencyIcon type="primary" />
              </div>
              <p
                className="text text_type_main-small pb-6"
                style={{ textAlign: "center" }}
              >
                Соус традиционный галактический
              </p>
              <Counter count={1} size="default" extraClass="m-1" />
            </div>
            <div
              className="mb-10"
              style={{
                width: "min-content",
                position: "relative",
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
              }}
